import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular_demo';
  name = "nasir";
  num = 45;
  arr = [4,5,6]
  imageUrl = "https://lh3.googleusercontent.com/a/AEdFTp5tvpDeHnT_yE9DpdijXv1GUpA6Y7D0DWZjOvWGww=s288-p-rw-no"
  imageWidth = 100;
  imageHeight = 100;
}
<div class="imageDiv">
    <img  [src] = "imageUrl" alt="uploading image" [width]="imageWidth" [height]="imageHeight">
</div>
<div class="nameDiv">
  <h1>Muhammad Nasir</h1>
</div>
<div class="centerDive">
    <h4>muhammadnairbaloch786@gmail.com</h4>
  </div>

