import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular_demo';
  name = "nasir";
  num = 45;
  arr = [4,5,6]
  imageUrl = "https://lh3.googleusercontent.com/a/AEdFTp5tvpDeHnT_yE9DpdijXv1GUpA6Y7D0DWZjOvWGww=s288-p-rw-no"
  imageWidth = 100;
  imageHeight = 100;
}
{{ name }}

<br />
<br />
<br />
<img class="profileImage" [src] = "imageUrl" alt="uploading image" [width]="imageWidth" [height]="imageHeight">
